import React, { Component } from "react";
import "./visual.css";
import { getMergeSortAnimations } from "../sortingVisualizer/algo";
import { Navbar, Button, Form, InputGroup, FormControl } from "react-bootstrap";
// Change this value for the speed of the animations.
const ANIMATION_SPEED_MS = 12;

// Change this value for the number of bars (value) in the array.
const NUMBER_OF_ARRAY_BARS = 310;

// This is the main color of the array bars.
const PRIMARY_COLOR = "turquoise";

// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = "red";
class Visualizer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arr: [],
    };
  }
  componentDidMount() {
    this.resetArray();
  }
  resetArray() {
    const arr = [];
    for (let i = 0; i < 40; i++) {
      arr.push(Math.floor(Math.random() * 670 + 5));
    }
    this.setState({ arr });
  }
  mergeSort() {
    const animations = getMergeSortAnimations(this.state.arr);
    for (let i = 0; i < animations.length; i++) {
      const arrayBars = document.getElementsByClassName("array-bar");
      const isColorChange = i % 3 !== 2; //every two iterations
      if (isColorChange) {
        const [barOneIdx, barTwoIdx] = animations[i];
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;
        const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
        setTimeout(() => {
          barOneStyle.backgroundColor = color;
          barTwoStyle.backgroundColor = color;
        }, i * ANIMATION_SPEED_MS);
      } else {
        setTimeout(() => {
          const [barOneIdx, newHeight] = animations[i];
          const barOneStyle = arrayBars[barOneIdx].style;
          barOneStyle.height = `${newHeight}px`;
        }, i * ANIMATION_SPEED_MS);
      }
    }
  }
  changeColor() {
    const arrayBars = document.getElementsByClassName("array-bar");
    const height = 10;
    for (let i = 0; i < arrayBars.length; i++) {
      const barOneStyle = arrayBars[i].style;
      setTimeout(() => {
        barOneStyle.backgroundColor = SECONDARY_COLOR;
      }, i * ANIMATION_SPEED_MS);
    }
  }

  bubleSort() {
    const arr = document.getElementsByClassName("array-bar");
    let min_idx = 0;
    for (let i = 0; i < arr.length - 1; i++) {
      // Find the minimum element in unsorted array
      min_idx = i;
      for (let j = i + 1; j < arr.length; j++)
        if (arr[j].style.height < arr[min_idx].style.height) min_idx = j;

      // Swap the found minimum element with the first element
      let temp = arr[min_idx].style.height;
      arr[min_idx].style.height = arr[i].style.height;
      arr[i].style.height = temp;
    }
  }
  swap(xp, yp) {
    let temp = xp;
    xp = yp;
    yp = temp;
  }

  swapHeight(barOneStyle, barTwoStyle) {
    const temp = barOneStyle.height;
    barOneStyle.height = barTwoStyle.height;
    barTwoStyle.height = temp;
  }

  render() {
    const { arr } = this.state;
    return (
      <div className="array-container">
        <Navbar className="bg-light justify-content-between">
          <Form inline>
            <InputGroup>
              <Form inline>
                <h1>sortingVisualizer</h1>
              </Form>
            </InputGroup>
          </Form>
          <Form inline>
            <Button className="btn btn-info" onClick={() => this.resetArray()}>
              generate new array
            </Button>
          </Form>
          <Form inline>
            <Button className="btn btn-info" onClick={() => this.changeColor()}>
              changeArrayColor
            </Button>
          </Form>
          <Form inline>
            <Button className="btn btn-info" onClick={() => this.bubleSort()}>
              bubbleSort
            </Button>
          </Form>

          <Form inline>
            <Button className="btn btn-info" onClick={() => this.mergeSort()}>
              mergeSort
            </Button>
          </Form>
        </Navbar>

        {arr.map((num, idx) => (
          <div
            className="array-bar"
            key={idx}
            style={{ height: `${num}px` }}
          ></div>
        ))}
      </div>
    );
  }
}

export default Visualizer;
