import React, { Component } from "react";
class visualizer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arr: [],
    };
  }
  componentDidMount() {
    this.resetArray();
  }
  resetArray() {
    const array = [];
    for (let i = 0; i < 100; i++) {
      array.push(Math.floor(Math.random() * 1000 + 5));
    }
    this.setState({ array });
  }
  render() {
    const { array } = this.state;
    return (
      <>
        {array.map((num) => (
          <div className="array-bar" key={num}>
            {num}
          </div>
        ))}
      </>
    );
  }
}

export default visualizer;
