import React, { Component } from "react";

class Visualizer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arr: [],
    };
  }
  componentDidMount() {
    this.resetArray();
  }
  resetArray() {
    const arr = [];
    for (let i = 0; i < 100; i++) {
      array.push(Math.floor(Math.random() * 1000 + 5));
    }
    this.setState({ arr });
  }
  render() {
    const { arr } = this.state;
    return (
      <>
        {arr.map((num, idx) => (
          <div className="array-bar" key={idx}>
            {num}
          </div>
        ))}
      </>
    );
  }
}

export default Visualizer;
